function init(){var t=document.getElementsByTagName("div").left,r=document.getElementsByTagName("div").right;t.clientHeight>r.clientHeight?t.classList.add("left"):r.classList.add("right")}function reset(t,r){for(let a in t){let s=t[a],n=Array.from(s.word.toUpperCase()),l=Number(a.substring(0,a.length-1));if(r[s.startY][s.startX]=l,"d"==a.charAt(a.length-1))for(var e=0;e<n.length;e++)r[s.startY+e+1][s.startX]="|";else for(e=0;e<n.length;e++)r[s.startY][s.startX+e+1]="|"}}function reveal(t,r,e){let a=t[e],s=Array.from(a.word.toUpperCase());if("d"==e.charAt(e.length-1))for(var n=0;n<s.length;n++){let t=s[n].toString();32==t.charCodeAt(0)&&(t="*"),r[a.startY+n+1][a.startX]=t}else for(n=0;n<s.length;n++){let t=s[n].toString();32==t.charCodeAt(0)&&(t="*"),r[a.startY][a.startX+n+1]=t}}function getCells(t,r){let e=[],a=[],s=t[r],n=Array.from(s.word.toUpperCase());if("d"==r.charAt(r.length-1))for(var l=0;l<n.length;l++)a.push(s.startY+l+1),e.push(s.startX);else for(l=0;l<n.length;l++)a.push(s.startY),e.push(s.startX+l+1);return{y:a,x:e}}function fullPopulate(t,r){for(let a in t){let s=t[a],n=Array.from(s.word.toUpperCase()),l=a.substring(0,a.length-1);if(r[s.startY][s.startX]=l,"d"==a.charAt(a.length-1))for(var e=0;e<n.length;e++){let t=n[e].toString();32==t.charCodeAt(0)&&(t="*"),r[s.startY+e+1][s.startX]=t}else for(e=0;e<n.length;e++){let t=n[e].toString();32==t.charCodeAt(0)&&(t="*"),r[s.startY][s.startX+e+1]=t}}}document.addEventListener("DOMContentLoaded",init,!1);